        {
            try
            {
                switch (e.KeyCode)
                {
                    case Keys.Enter:
                        if (m_fg.Focused)
                        {
                            switch (m_e_form_mode)
                            {
                                    case DataEntryFormMode.SelectDataState:
                                    //select_data_2_us();
                                    break;

                            }
                        }
                        break;
                }
            }
            catch (Exception v_e)
            {
                CSystemLog_301.ExceptionHandle(v_e);
            }
        }

