			m_us_v_dm_don_vi_tinh.FillDataset(m_ds_v_dm_don_vi_tinh);
			m_fg.Redraw = false;
			CGridUtils.Dataset2C1Grid(m_ds_v_dm_don_vi_tinh, m_fg, m_obj_trans);
			m_fg.Redraw = true;
		}
