        private void set_initial_form_load()
        {
            m_obj_trans = get_trans_object(m_fg);
            load_data_2_grid();
        }
        private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(BKI_QLHT.DS.CDBNames.V_BC_DOANH_THU_NGAY_KH.MA_GIAO_DICH, e_col_Number.MA_GIAO_DICH);
            v_htb.Add(BKI_QLHT.DS.CDBNames.V_BC_DOANH_THU_NGAY_KH.MA_KHACH_HANG, e_col_Number.MA_KHACH_HANG);
            v_htb.Add(BKI_QLHT.DS.CDBNames.V_BC_DOANH_THU_NGAY_KH.TEN_KHACH_HANG, e_col_Number.TEN_KHACH_HANG);
            v_htb.Add(BKI_QLHT.DS.CDBNames.V_BC_DOANH_THU_NGAY_KH.DOANH_THU, e_col_Number.DOANH_THU);
            v_htb.Add(BKI_QLHT.DS.CDBNames.V_BC_DOANH_THU_NGAY_KH.NGAY_GIAO_DICH, e_col_Number.NGAY_GIAO_DICH);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_v_ds._V_BC_DOANH_THU_NGAY_KH.NewRow());
            return v_obj_trans;
        }
        private void load_data_2_grid()
        {
            m_v_ds.Clear();
            m_v_us.FillDatasetSearch(m_v_ds, "");
            decimal v_dc_tong_doanh_thu = 0;
            //m_v_us.get_tong_doanh_thu(v_dc_tong_doanh_thu);
            for (int i = 0; i < m_v_ds._V_BC_DOANH_THU_NGAY_KH.Count; i++)
            {
                v_dc_tong_doanh_thu += CIPConvert.ToDecimal(m_v_ds.Tables[0].Rows[i][BKI_QLHT.DS.CDBNames
                .V_BC_DOANH_THU_NGAY_KH.DOANH_THU]);
            }
            m_lbl_tong_doanh_thu.Text = CIPConvert.ToStr(v_dc_tong_doanh_thu);
            m_fg.Redraw = true;
            
        }
        private void grid2us_object(US_V_BC_DOANH_THU_NGAY_KH i_us
            , int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }


        private void us_object2grid(US_V_BC_DOANH_THU_NGAY_KH i_us
            , int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
        }

        private void update_to_sever(){
            m_pcb_run.Value = 0;
            m_pcb_run.Visible = true;
            
            string date_time = m_lbl_ngay_giao_dich.Text;

            string tong_tien = m_lbl_tong_doanh_thu.Text;
            
            string url = "http://iapgroup.vn/MMS_android_api/Put_data_from_local_to_server/MMS_Insert.php?ngay=" + date_time + "&tongtien=" + tong_tien;
            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);
           
            // Set some reasonable limits on resources used by this request
            request.MaximumAutomaticRedirections = 4;
            request.MaximumResponseHeadersLength = 4;
           
            // Set credentials to use for this request.
            request.Credentials = CredentialCache.DefaultCredentials;
            HttpWebResponse response = (HttpWebResponse)request.GetResponse();
            
            // Get the stream associated with the response.
            Stream receiveStream = response.GetResponseStream();
           
            // Pipes the stream to a higher level stream reader with the required encoding format. 
            StreamReader readStream = new StreamReader(receiveStream, Encoding.UTF8);
            
            m_pcb_run.Visible = false;
            response.Close();
            readStream.Close();
            //MessageBox.Show("Cập nhật thành công");
        }
        private void set_define_events()
        {
            m_cmd_exit.Click += new EventHandler(m_cmd_exit_Click);
            m_cmd_update.Click += new EventHandler(m_cmd_update_Click);
        }

