		private void f401_update_to_sever_Load(object sender, System.EventArgs e) {
			try{
				set_initial_form_load();
                load_data_2_lbl();
                f403_InternetConection v_internetconect = new f403_InternetConection();
                if (v_internetconect.IsConnectedToInternet())
                {
                    m_lbl_conect.Text = "Hiện đang kết nối mạng";
                }
                else
                {
                    m_lbl_conect.Text = "Không có mạng";
                }
                
            }
            catch (Exception v_e)
            {
                CSystemLog_301.ExceptionHandle(v_e);
            }

        }

        private void m_cmd_exit_Click(object sender, EventArgs e)
        {
            try
            {
                this.Close();
            }
            catch (Exception v_e)
            {
                CSystemLog_301.ExceptionHandle(v_e);
            }
        }

        private void m_cmd_update_Click(object sender, EventArgs e)
        {
            try
            {
                update_to_sever();
                this.Close();
            }
            catch (System.Exception ex)
            {
                CSystemLog_301.ExceptionHandle(ex);
            }
        }

        private void m_timer_Tick(object sender, EventArgs e)
        {
            m_pcb_run.Value ++;
            if (m_pcb_run.Value == 100)
                m_pcb_run.Value = m_pcb_run.Value + 0;

        }
        
      

        
