        private void load_data_cbo_form_name()
        {
            US_HT_FORM v_us_ht_form = new US_HT_FORM();
            DS_HT_FORM v_ds_ht_form = new DS_HT_FORM();
            v_us_ht_form.FillDataset(v_ds_ht_form, "order by form_name");
            m_cbo_ten_form.DataSource = v_ds_ht_form.HT_FORM;
            m_cbo_ten_form.ValueMember = HT_FORM.ID;
            m_cbo_ten_form.DisplayMember = HT_FORM.FORM_NAME;
        }
        private void format_controls()
        {
            CControlFormat.setFormStyle(this, new CAppContext_201());
            CControlFormat.setC1FlexFormat(m_fg);
            CGridUtils.AddSave_Excel_Handlers(m_fg);
            CGridUtils.AddSearch_Handlers(m_fg);
            set_define_events();
            this.KeyPreview = true;
        }

        private void set_initial_form_load()
        {
            format_controls();
            load_data_cbo_form_name();
            load_control_unlist();
            load_control_list();
            m_obj_trans = get_trans_object(m_fg);
            load_data_2_grid();
        }
        private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(V_HT_CONTROL_IN_FORM.FORM_NAME, e_col_Number.FORM_NAME);
            v_htb.Add(V_HT_CONTROL_IN_FORM.MA_TU_DIEN, e_col_Number.MA_TU_DIEN);
            v_htb.Add(V_HT_CONTROL_IN_FORM.DISPLAY_NAME, e_col_Number.DISPLAY_NAME);
            v_htb.Add(V_HT_CONTROL_IN_FORM.CONTROL_TYPE, e_col_Number.CONTROL_TYPE);
            v_htb.Add(V_HT_CONTROL_IN_FORM.ID_TU_DIEN, e_col_Number.ID_TU_DIEN);
            v_htb.Add(V_HT_CONTROL_IN_FORM.ID, e_col_Number.ID);
            v_htb.Add(V_HT_CONTROL_IN_FORM.CONTROL_NAME, e_col_Number.CONTROL_NAME);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.V_HT_CONTROL_IN_FORM.NewRow());
            return v_obj_trans;
        }
        private void load_data_2_grid()
        {
            m_ds = new DS_V_HT_CONTROL_IN_FORM();
            m_us.FillDataset(m_ds);
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            m_fg.Redraw = true;
        }
        private void grid2us_object(US_V_HT_CONTROL_IN_FORM i_us
            , int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }
        private void us_object2grid(US_V_HT_CONTROL_IN_FORM i_us
            , int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
        }


