		//	v_fDE.display(m_us);
		}
        private void load_data_2_cbo() {
            US_DM_DANH_MUC_THUOC v_us_dm = new US_DM_DANH_MUC_THUOC();
            DS_DM_DANH_MUC_THUOC v_ds_dm = new DS_DM_DANH_MUC_THUOC();
            v_us_dm.FillDataset(v_ds_dm);
            m_cbo_danh_muc_thuoc.DataSource = v_ds_dm.DM_DANH_MUC_THUOC;
            m_cbo_danh_muc_thuoc.ValueMember = DM_DANH_MUC_THUOC.ID;
            m_cbo_danh_muc_thuoc.DisplayMember = DM_DANH_MUC_THUOC.TEN_DANH_MUC;

        }
        private void load_data_2_cbo_tk() {
            US_DM_DANH_MUC_THUOC v_us_dm = new US_DM_DANH_MUC_THUOC();
            DS_DM_DANH_MUC_THUOC v_ds_dm = new DS_DM_DANH_MUC_THUOC();
            v_us_dm.FillDataset(v_ds_dm);

            m_cbo_tk_danh_muc.DataSource = v_ds_dm.DM_DANH_MUC_THUOC;
            m_cbo_tk_danh_muc.ValueMember = DM_DANH_MUC_THUOC.ID;
            m_cbo_tk_danh_muc.DisplayMember = DM_DANH_MUC_THUOC.TEN_DANH_MUC;
            DataRow v_dr = v_ds_dm.DM_DANH_MUC_THUOC.NewRow();
            v_dr[DM_DANH_MUC_THUOC.ID] = -1;
            v_dr[DM_DANH_MUC_THUOC.TEN_DANH_MUC] = "----tat ca----";
            v_dr[DM_DANH_MUC_THUOC.GHI_CHU] = "";
            v_ds_dm.DM_DANH_MUC_THUOC.Rows.InsertAt(v_dr, 0);
            m_cbo_tk_danh_muc.SelectedIndex = 0;
        }
        private string get_danh_muc_thuoc() {
            if (m_cbo_tk_danh_muc.SelectedIndex != 0) {
                return m_cbo_tk_danh_muc.Text;
            }
            return "";
        }
        private void form_2_us_obj()
        {
            m_us_nhom_thuoc.strTEN_NHOM = m_txt_ten_nhom_thuoc.Text;
            //m_us.strTEN_DANH_MUC=m_cbo_danh_muc_thuoc.Text;
            m_us_nhom_thuoc.strGHI_CHU = m_txt_ghi_chu.Text;
            m_us_nhom_thuoc.dcID_DANH_MUC_THUOC = CIPConvert.ToDecimal(m_cbo_danh_muc_thuoc.SelectedValue);
        }
        private void us_obj_2_form(US_V_DM_NHOM_THUOC ip_us_v_dm)
        {
            m_us_nhom_thuoc.dcID = ip_us_v_dm.dcID;
            m_cbo_danh_muc_thuoc.SelectedValue = ip_us_v_dm.dcID_DANH_MUC_THUOC;
            m_txt_ten_nhom_thuoc.Text = ip_us_v_dm.strTEN_NHOM;
            m_txt_ghi_chu.Text = ip_us_v_dm.strGHI_CHU;
        }
        private void save_data()
        {
            form_2_us_obj();
            switch (m_e_for_mode)
            {
                case DataEntryFormMode.InsertDataState:
                    m_us_nhom_thuoc.Insert();
                    break;
                case DataEntryFormMode.UpdateDataState:
                    m_us_nhom_thuoc.Update();
                    break;
            }
            BaseMessages.MsgBox_Infor("Đã cập nhật thành công");
            xoa_trang();
            load_data_2_grid();
        }

        private void xoa_trang()
        {
            m_txt_ghi_chu.Clear();
            m_txt_ten_nhom_thuoc.Clear();
            m_cbo_danh_muc_thuoc.SelectedIndex = 0;
            m_e_for_mode = DataEntryFormMode.InsertDataState;
        }
